import React from 'react';
import './Task.css';

const Task = ({ task }) => {
  const { title, status, priority, deadline } = task;

  return (
    <div className={`task ${status}`}>
      <h2>{title}</h2>
      <p>Status: {status}</p>
      <p>Priority: {priority}</p>
      <p>Deadline: {deadline}</p>
    </div>
  );
};

export default Task;
